package skill1;
public class datatypes8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte personAge = 35;

        short currentYear = 2025;

        int countryPopulation = 1428000000;

        long distanceToMoon = 384400000L;

        float piValue = 3.14159f;

        double gravityAcceleration = 9.80665;

        char studentGrade = 'A';

        boolean isJavaFun = true;

        System.out.println("Person's Age: " + personAge);
        System.out.println("Current Year: " + currentYear);
        System.out.println("Country Population: " + countryPopulation);
        System.out.println("Distance to the Moon (in meters): " + distanceToMoon);
        System.out.println("Value of Pi: " + piValue);
        System.out.println("Acceleration due to Gravity (m/s^2): " + gravityAcceleration);
        System.out.println("Student's Grade: " + studentGrade);
        System.out.println("Is Java fun to learn? " + isJavaFun);
        
	}

}
